SELECT co.CourseOfferId, co.CourseId, co.Year, co.Quartile, c.CourseName, c.CourseDescription, c.DegreeId, c.ECTS, d.Dept, d.DegreeDescription, d.TotalECTS, t.TeacherId, t.TeacherName, t.Address, t.BirthyearTeacher, t.Gender FROM CourseOffers co, Courses c, Degrees d, TeacherAssignmentsToCourses tatc, Teachers t WHERE co.CourseOfferId = 1 and co.CourseId = c.CourseId and c.DegreeId = d.DegreeId and co.CourseOfferId = tatc.CourseOfferId and tatc.TeacherId = t.TeacherId;
SELECT co.CourseOfferId, co.CourseId, co.Year, co.Quartile, s.StudentId, s.StudentName, s.Address, s.BirthyearStudent, s.Gender, d.DegreeId, d.Dept, d.DegreeDescription, d.TotalECTS FROM StudentRegistrationsToDegrees srtd, StudentAssistants sa, CourseOffers co, Students s, Degrees d WHERE srtd.StudentRegistrationId = 140 and srtd.StudentRegistrationId = sa.StudentRegistrationId and sa.CourseOfferId = co.CourseOfferId and srtd.StudentId = s.StudentId and srtd.DegreeId = d.DegreeId;
SELECT avg(grade) FROM CourseRegistrations WHERE StudentRegistrationId = 140;
